import org.w3c.dom.Document;import org.w3c.dom.NamedNodeMap;import org.w3c.dom.Node;import org.w3c.dom.NodeList;import javax.xml.parsers.SAXParser;import javax.xml.parsers.SAXParserFactory;import java.io.File;import java.text.SimpleDateFormat;import java.util.Date;import java.util.HashMap;public class Loader {    private static final int CONVERT_MEMORY = 1024 * 1024;    private static final int CONVERT_TIME = 1000;    public static void main(String[] args) throws Exception {        long usedMemory = Runtime.getRuntime().totalMemory() - Runtime.getRuntime().freeMemory();        long start = System.currentTimeMillis();        System.out.println("start program - " + usedMemory / CONVERT_MEMORY + " Mb");        String fileName = "res/data-1572M.xml";        parseFile(fileName);        DBConnection.executeMultiInsert();        usedMemory = Runtime.getRuntime().totalMemory() - Runtime.getRuntime().freeMemory() - usedMemory;        System.out.println("end program - " + usedMemory / CONVERT_MEMORY + " Mb \n\t" +                "Runtime - " + ((System.currentTimeMillis() - start) / CONVERT_TIME) + " sec");    }    private static void parseFile(String fileName) throws Exception {        SAXParserFactory factory = SAXParserFactory.newInstance();        SAXParser parser = factory.newSAXParser();        XMLHandler handler = new XMLHandler();        parser.parse(new File(fileName), handler);    }}